mapper.select:
	 select  count(*) from  erp_rec rec               


                 rec.no like concat('%',#{no,jdbcType=VARCHAR},'%')


                 and rec.dep=#{depid,jdbcType=VARCHAR}


                 and rec.vendor=#{vendorid,jdbcType=VARCHAR}

select rec.id as id, rec.no as no, bdep.id as depid, bdep.department_desc as depdesc, bvendor.id as vendorid, bvendor.vendor_desc as vendordesc, rec.amtrp as amtrp, rec.amtsp as amtsp, rec.create_by as "createBy", createruser.id as "createBy.id", createruser.login_name as "createBy.name", rec.create_date as "createDate", rec.update_by as "updateBy", updateuser.id as "updateBy.id", updateuser.login_name as "updateBy.name", rec.update_date as "updateDate", rec.audit_flag as "auditFlag", rec.audit_by as "auditBy", audituser.id as "auditBy.id", audituser.login_name as "auditBy.name", rec.remarks AS "remarks", rec.del_flag AS "delFlag" from erp_rec rec left join erp_department bdep on rec.dep=bdep.id left join erp_vendor bvendor on rec.vendor=bvendor.id left join sys_user createruser on rec.create_by=createruser.id left join sys_user updateuser on rec.update_by=updateuser.id left join sys_user audituser on rec.audit_by=audituser.id
rec.no like concat('%',#{no,jdbcType=VARCHAR},'%')
and rec.dep=#{depid,jdbcType=VARCHAR}
and rec.vendor=#{vendorid,jdbcType=VARCHAR}
limit #{start},#{pagesize}
